    private void edSignoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_edSignoActionPerformed
 String tratamento, nome;
   double sexo, dia, mes, ano;
   nome = edNome.getText();
   dia=Double.parseDouble(edDia.getText());
   mes=Double.parseDouble(edMes.getText());
   ano=Double.parseDouble(edAno.getText());
   if(ldMasc.isSelected()){ 
       tratamento="senhor";     
   }else{
   tratamento="senhora";
         
   } 
   
   JOptionPane.showMessageDialog(null, "Boa tarde "+ tratamento + " "+ nome);
    }//GEN-LAST:event_edSignoActionPerformed

